import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  backdrop-filter: blur(10px);
  padding: 20px;
  height: 400px;
`;

const Title = styled.h3`
  margin-bottom: 15px;
  color: #a0aec0;
  font-size: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const TimeFrameButtons = styled.div`
  display: flex;
  gap: 5px;
`;

const TimeButton = styled.button`
  padding: 5px 10px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: ${props => props.active ? 'rgba(102, 126, 234, 0.3)' : 'transparent'};
  color: white;
  border-radius: 4px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(102, 126, 234, 0.2);
  }
`;

const ChartContainer = styled.div`
  height: 300px;
  width: 100%;
`;

const TradingChart = ({ selectedCrypto, cryptoData }) => {
  const [timeFrame, setTimeFrame] = useState('1H');
  const [chartData, setChartData] = useState([]);
  const [lastPrice, setLastPrice] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(0);
  const [isRealtime, setIsRealtime] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  // H√†m l√†m m∆∞·ª£t gi√° ƒë·ªÉ tr√°nh nh·∫£y d·ªØ
  const smoothPrice = (newPrice, lastPrice) => {
    if (!lastPrice) return newPrice;

    // Ch·ªâ c·∫≠p nh·∫≠t n·∫øu thay ƒë·ªïi > 0.1%
    const changePercent = Math.abs((newPrice - lastPrice) / lastPrice) * 100;
    if (changePercent < 0.1) {
      return lastPrice;
    }

    // Smoothing v·ªõi weighted average
    return lastPrice * 0.7 + newPrice * 0.3;
  };

  // T·∫°o d·ªØ li·ªáu bi·ªÉu ƒë·ªì t·ª´ gi√° th·∫≠t
  useEffect(() => {
    const currentPrice = cryptoData[selectedCrypto]?.price;
    if (!currentPrice) return;

    const now = Date.now();

    // Debounce: ch·ªâ c·∫≠p nh·∫≠t m·ªói 2 gi√¢y v√† ch·ªâ khi realtime ƒë∆∞·ª£c b·∫≠t
    if (!isRealtime || now - lastUpdate < 2000) return;
    setLastUpdate(now);

    // L√†m m∆∞·ª£t gi√°
    const smoothedPrice = smoothPrice(currentPrice, lastPrice);
    setLastPrice(smoothedPrice);

    const newDataPoint = {
      time: new Date().toLocaleTimeString('vi-VN', { 
        hour: '2-digit', 
        minute: '2-digit',
        second: '2-digit'
      }),
      price: parseFloat(smoothedPrice.toFixed(2)),
      volume: cryptoData[selectedCrypto]?.volume || 0
    };

    setChartData(prevData => {
      const updatedData = [...prevData, newDataPoint];

      // Gi·ªØ t·ªëi ƒëa 100 ƒëi·ªÉm d·ªØ li·ªáu
      if (updatedData.length > 100) {
        return updatedData.slice(-100);
      }

      return updatedData;
    });
  }, [cryptoData[selectedCrypto]?.price, selectedCrypto, lastPrice, lastUpdate]);

  // L·∫•y d·ªØ li·ªáu l·ªãch s·ª≠ th·∫≠t t·ª´ API
  useEffect(() => {
    const fetchHistoryData = async () => {
      setIsLoading(true);
      try {
        const response = await fetch(`/api/history/${selectedCrypto}`);
        const data = await response.json();

        if (data.error) {
          console.error('L·ªói khi l·∫•y d·ªØ li·ªáu l·ªãch s·ª≠:', data.error);
          return;
        }

        if (data.length > 0) {
          setChartData(data);
          setLastPrice(data[data.length - 1].price);
        }
      } catch (error) {
        console.error('L·ªói khi fetch d·ªØ li·ªáu l·ªãch s·ª≠:', error);
      } finally {
        setIsLoading(false);
      }
    };

    if (selectedCrypto) {
      fetchHistoryData();
    }
  }, [selectedCrypto]);

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      const price = payload[0].value;
      const volume = payload[0].payload.volume;

      return (
        <div style={{
          backgroundColor: 'rgba(0, 0, 0, 0.9)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '8px',
          padding: '12px',
          color: 'white',
          fontSize: '12px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)'
        }}>
          <p style={{ margin: '0 0 8px 0', fontWeight: 'bold' }}>{`‚è∞ ${label}`}</p>
          <p style={{ color: '#00ff88', margin: '4px 0', fontWeight: 'bold' }}>
            {`üí∞ $${price.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`}
          </p>
          <p style={{ color: '#a0aec0', margin: '4px 0' }}>
            {`üìä Volume: ${volume.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`}
          </p>
        </div>
      );
    }
    return null;
  };

  return (
    <Container>
      <Title>
        üìà Bi·ªÉu ƒë·ªì gi√° - {selectedCrypto}
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <TimeFrameButtons>
            {['1H', '4H', '1D', '1W'].map(tf => (
              <TimeButton
                key={tf}
                active={timeFrame === tf}
                onClick={() => setTimeFrame(tf)}
              >
                {tf}
              </TimeButton>
            ))}
          </TimeFrameButtons>
          <TimeButton
            active={isRealtime}
            onClick={() => setIsRealtime(!isRealtime)}
            style={{ fontSize: '10px', padding: '4px 8px' }}
          >
            {isRealtime ? 'üü¢ Live' : 'üî¥ Pause'}
          </TimeButton>
          <TimeButton
            onClick={() => {
              setChartData([]);
              setLastPrice(null);
              // Trigger reload
              const event = new Event('reload');
              window.dispatchEvent(event);
            }}
            style={{ fontSize: '10px', padding: '4px 8px' }}
          >
            üîÑ Refresh
          </TimeButton>
        </div>
      </Title>

      <ChartContainer>
        <div style={{ 
          position: 'absolute', 
          top: '10px', 
          right: '10px', 
          fontSize: '12px', 
          color: isRealtime ? '#00ff88' : '#a0aec0',
          zIndex: 10 
        }}>
          {isRealtime ? 'üü¢ Live' : 'üî¥ Paused'}
        </div>

        {isLoading ? (
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100%',
            color: '#a0aec0',
            fontSize: '14px'
          }}>
            üìä ƒêang t·∫£i d·ªØ li·ªáu l·ªãch s·ª≠...
          </div>
        ) : chartData.length === 0 ? (
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100%',
            color: '#a0aec0',
            fontSize: '14px'
          }}>
            üìä Kh√¥ng c√≥ d·ªØ li·ªáu cho {selectedCrypto}
          </div>
        ) : (
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.1)" />
              <XAxis 
                dataKey="time" 
                stroke="#a0aec0"
                fontSize={12}
                tick={{ fill: '#a0aec0' }}
              />
                          <YAxis 
              stroke="#a0aec0"
              fontSize={12}
              tick={{ fill: '#a0aec0' }}
              domain={['auto', 'auto']}
              tickFormatter={(value) => `$${value.toLocaleString()}`}
            />
              <Tooltip content={<CustomTooltip />} />
              <Line 
                type="monotone" 
                dataKey="price" 
                stroke="#00ff88" 
                strokeWidth={2}
                dot={false}
                activeDot={{ r: 4, fill: '#00ff88' }}
                connectNulls={true}
                isAnimationActive={false}
              />
            </LineChart>
          </ResponsiveContainer>
        )}
      </ChartContainer>
    </Container>
  );
};

export default TradingChart;
