import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import styled from 'styled-components';
import CryptoList from './components/CryptoList';
import TradingChart from './components/TradingChart';
import OrderBook from './components/OrderBook';
import TradingForm from './components/TradingForm';
import TradingHistory from './components/TradingHistory';
import Header from './components/Header';

const socket = io('http://localhost:5001');

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #0a0e1a 0%, #1a1f2e 100%);
  color: white;
`;

const MainContent = styled.div`
  display: grid;
  grid-template-columns: 300px 1fr 350px;
  grid-template-rows: auto 1fr;
  gap: 20px;
  padding: 20px;
  height: calc(100vh - 80px);

  @media (max-width: 1200px) {
    grid-template-columns: 250px 1fr 300px;
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    grid-template-rows: auto auto auto auto auto;
    height: auto;
  }
`;

const LeftPanel = styled.div`
  grid-row: 1 / 3;
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (max-width: 768px) {
    grid-row: auto;
  }
`;

const CenterPanel = styled.div`
  grid-row: 1 / 3;
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (max-width: 768px) {
    grid-row: auto;
  }
`;

const RightPanel = styled.div`
  grid-row: 1 / 3;
  display: flex;
  flex-direction: column;
  gap: 20px;

  @media (max-width: 768px) {
    grid-row: auto;
  }
`;

function App() {
  const [cryptoData, setCryptoData] = useState({});
  const [orderBooks, setOrderBooks] = useState({});
  const [tradingHistory, setTradingHistory] = useState([]);
  const [selectedCrypto, setSelectedCrypto] = useState('BTC');
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Kết nối Socket.IO
    socket.on('connect', () => {
      console.log('Đã kết nối với server');
      setIsConnected(true);
    });

    socket.on('disconnect', () => {
      console.log('Mất kết nối với server');
      setIsConnected(false);
    });

    // Lắng nghe cập nhật dữ liệu crypto
    socket.on('cryptoUpdate', (data) => {
      setCryptoData(data);
    });

    // Lắng nghe cập nhật order book
    socket.on('orderBookUpdate', (data) => {
      setOrderBooks(data);
    });

    // Lắng nghe cập nhật lịch sử giao dịch
    socket.on('tradingHistory', (data) => {
      setTradingHistory(data);
    });

    socket.on('newOrder', (order) => {
      setTradingHistory(prev => [...prev, order]);
    });

    socket.on('orderCancelled', (orderId) => {
      setTradingHistory(prev => 
        prev.map(order => 
          order.id === orderId 
            ? { ...order, status: 'cancelled' }
            : order
        )
      );
    });

    return () => {
      socket.off('connect');
      socket.off('disconnect');
      socket.off('cryptoUpdate');
      socket.off('orderBookUpdate');
      socket.off('tradingHistory');
      socket.off('newOrder');
      socket.off('orderCancelled');
    };
  }, []);

  const handlePlaceOrder = (orderData) => {
    socket.emit('placeOrder', {
      ...orderData,
      symbol: selectedCrypto
    });
  };

  const handleCancelOrder = (orderId) => {
    socket.emit('cancelOrder', orderId);
  };

  return (
    <AppContainer>
      <Header isConnected={isConnected} />
      <MainContent>
        <LeftPanel>
          <CryptoList 
            cryptoData={cryptoData} 
            selectedCrypto={selectedCrypto}
            onSelectCrypto={setSelectedCrypto}
          />
          <TradingForm 
            selectedCrypto={selectedCrypto}
            currentPrice={cryptoData[selectedCrypto]?.price}
            onPlaceOrder={handlePlaceOrder}
          />
        </LeftPanel>

        <CenterPanel>
          <TradingChart 
            selectedCrypto={selectedCrypto}
            cryptoData={cryptoData}
          />
          <TradingHistory 
            history={tradingHistory}
            onCancelOrder={handleCancelOrder}
          />
        </CenterPanel>

        <RightPanel>
          <OrderBook 
            orderBook={orderBooks[selectedCrypto]}
            selectedCrypto={selectedCrypto}
          />
        </RightPanel>
      </MainContent>
    </AppContainer>
  );
}

export default App;
