import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  backdrop-filter: blur(10px);
  padding: 20px;
  height: 400px;
`;

const Title = styled.h3`
  margin-bottom: 15px;
  color: #a0aec0;
  font-size: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const TimeFrameButtons = styled.div`
  display: flex;
  gap: 5px;
`;

const TimeButton = styled.button`
  padding: 5px 10px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: ${props => props.active ? 'rgba(102, 126, 234, 0.3)' : 'transparent'};
  color: white;
  border-radius: 4px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(102, 126, 234, 0.2);
  }
`;

const ChartContainer = styled.div`
  height: 300px;
  width: 100%;
`;

const TradingChart = ({ selectedCrypto, cryptoData }) => {
  const [timeFrame, setTimeFrame] = useState('1H');
  const [chartData, setChartData] = useState([]);
  const [lastPrice, setLastPrice] = useState(null);

  // H√†m l√†m m∆∞·ª£t gi√° ƒë·ªÉ tr√°nh nh·∫£y d·ªØ
  const smoothPrice = (newPrice, lastPrice) => {
    if (!lastPrice) return newPrice;

    // Ch·ªâ c·∫≠p nh·∫≠t n·∫øu thay ƒë·ªïi > 0.1%
    const changePercent = Math.abs((newPrice - lastPrice) / lastPrice) * 100;
    if (changePercent < 0.1) {
      return lastPrice;
    }

    // Smoothing v·ªõi weighted average
    return lastPrice * 0.7 + newPrice * 0.3;
  };

  // T·∫°o d·ªØ li·ªáu bi·ªÉu ƒë·ªì t·ª´ gi√° th·∫≠t
  useEffect(() => {
    const currentPrice = cryptoData[selectedCrypto]?.price;
    if (!currentPrice) return;

    // L√†m m∆∞·ª£t gi√°
    const smoothedPrice = smoothPrice(currentPrice, lastPrice);
    setLastPrice(smoothedPrice);

    const now = new Date();
    const newDataPoint = {
      time: now.toLocaleTimeString('vi-VN', { 
        hour: '2-digit', 
        minute: '2-digit',
        second: '2-digit'
      }),
      price: parseFloat(smoothedPrice.toFixed(2)),
      volume: cryptoData[selectedCrypto]?.volume || 0
    };

    setChartData(prevData => {
      const updatedData = [...prevData, newDataPoint];

      // Gi·ªØ t·ªëi ƒëa 100 ƒëi·ªÉm d·ªØ li·ªáu
      if (updatedData.length > 100) {
        return updatedData.slice(-100);
      }

      return updatedData;
    });
  }, [cryptoData[selectedCrypto]?.price, selectedCrypto, lastPrice]);

  // Kh·ªüi t·∫°o d·ªØ li·ªáu ban ƒë·∫ßu
  useEffect(() => {
    const currentPrice = cryptoData[selectedCrypto]?.price;
    if (!currentPrice) return;

    const data = [];
    const now = new Date();

    // T·∫°o 50 ƒëi·ªÉm d·ªØ li·ªáu ban ƒë·∫ßu v·ªõi gi√° hi·ªán t·∫°i
    for (let i = 50; i >= 0; i--) {
      const time = new Date(now.getTime() - i * 60000); // M·ªói ph√∫t
      data.push({
        time: time.toLocaleTimeString('vi-VN', { 
          hour: '2-digit', 
          minute: '2-digit' 
        }),
        price: parseFloat(currentPrice.toFixed(2)),
        volume: cryptoData[selectedCrypto]?.volume || 0
      });
    }

    setChartData(data);
  }, [selectedCrypto]);

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '6px',
          padding: '10px',
          color: 'white'
        }}>
          <p>{`Th·ªùi gian: ${label}`}</p>
          <p style={{ color: '#00ff88' }}>{`Gi√°: $${payload[0].value.toLocaleString()}`}</p>
          <p style={{ color: '#a0aec0' }}>{`Volume: ${payload[0].payload.volume.toFixed(0)}`}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <Container>
      <Title>
        üìà Bi·ªÉu ƒë·ªì gi√° - {selectedCrypto}
        <TimeFrameButtons>
          {['1H', '4H', '1D', '1W'].map(tf => (
            <TimeButton
              key={tf}
              active={timeFrame === tf}
              onClick={() => setTimeFrame(tf)}
            >
              {tf}
            </TimeButton>
          ))}
        </TimeFrameButtons>
      </Title>

      <ChartContainer>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.1)" />
            <XAxis 
              dataKey="time" 
              stroke="#a0aec0"
              fontSize={12}
              tick={{ fill: '#a0aec0' }}
            />
            <YAxis 
              stroke="#a0aec0"
              fontSize={12}
              tick={{ fill: '#a0aec0' }}
              domain={['dataMin - 100', 'dataMax + 100']}
            />
            <Tooltip content={<CustomTooltip />} />
            <Line 
              type="monotone" 
              dataKey="price" 
              stroke="#00ff88" 
              strokeWidth={2}
              dot={false}
              activeDot={{ r: 4, fill: '#00ff88' }}
              connectNulls={true}
              isAnimationActive={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </ChartContainer>
    </Container>
  );
};

export default TradingChart;
