import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  backdrop-filter: blur(10px);
  padding: 20px;
  height: 400px;
`;

const Title = styled.h3`
  margin-bottom: 15px;
  color: #a0aec0;
  font-size: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const TimeFrameButtons = styled.div`
  display: flex;
  gap: 5px;
`;

const TimeButton = styled.button`
  padding: 5px 10px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: ${props => props.active ? 'rgba(102, 126, 234, 0.3)' : 'transparent'};
  color: white;
  border-radius: 4px;
  font-size: 12px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(102, 126, 234, 0.2);
  }
`;

const ChartContainer = styled.div`
  height: 300px;
  width: 100%;
`;

const TradingChart = ({ selectedCrypto, cryptoData }) => {
  const [timeFrame, setTimeFrame] = useState('1H');
  const [chartData, setChartData] = useState([]);
  const [lastPrice, setLastPrice] = useState(null);
  const [lastUpdate, setLastUpdate] = useState(0);
  const [isRealtime, setIsRealtime] = useState(true);

  // Hàm làm mượt giá để tránh nhảy dữ
  const smoothPrice = (newPrice, lastPrice) => {
    if (!lastPrice) return newPrice;

    // Chỉ cập nhật nếu thay đổi > 0.1%
    const changePercent = Math.abs((newPrice - lastPrice) / lastPrice) * 100;
    if (changePercent < 0.1) {
      return lastPrice;
    }

    // Smoothing với weighted average
    return lastPrice * 0.7 + newPrice * 0.3;
  };

  // Tạo dữ liệu biểu đồ từ giá thật
  useEffect(() => {
    const currentPrice = cryptoData[selectedCrypto]?.price;
    if (!currentPrice) return;

    const now = Date.now();

    // Debounce: chỉ cập nhật mỗi 2 giây và chỉ khi realtime được bật
    if (!isRealtime || now - lastUpdate < 2000) return;
    setLastUpdate(now);

    // Làm mượt giá
    const smoothedPrice = smoothPrice(currentPrice, lastPrice);
    setLastPrice(smoothedPrice);

    const newDataPoint = {
      time: new Date().toLocaleTimeString('vi-VN', { 
        hour: '2-digit', 
        minute: '2-digit',
        second: '2-digit'
      }),
      price: parseFloat(smoothedPrice.toFixed(2)),
      volume: cryptoData[selectedCrypto]?.volume || 0
    };

    setChartData(prevData => {
      const updatedData = [...prevData, newDataPoint];

      // Giữ tối đa 100 điểm dữ liệu
      if (updatedData.length > 100) {
        return updatedData.slice(-100);
      }

      return updatedData;
    });
  }, [cryptoData[selectedCrypto]?.price, selectedCrypto, lastPrice, lastUpdate]);

  // Lấy dữ liệu lịch sử thật từ API
  useEffect(() => {
    const fetchHistoryData = async () => {
      try {
        const response = await fetch(`/api/history/${selectedCrypto}`);
        const data = await response.json();

        if (data.error) {
          console.error('Lỗi khi lấy dữ liệu lịch sử:', data.error);
          return;
        }

        if (data.length > 0) {
          setChartData(data);
          setLastPrice(data[data.length - 1].price);
        }
      } catch (error) {
        console.error('Lỗi khi fetch dữ liệu lịch sử:', error);
      }
    };

    if (selectedCrypto) {
      fetchHistoryData();
    }
  }, [selectedCrypto]);

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div style={{
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          borderRadius: '6px',
          padding: '10px',
          color: 'white'
        }}>
          <p>{`Thời gian: ${label}`}</p>
          <p style={{ color: '#00ff88' }}>{`Giá: $${payload[0].value.toLocaleString()}`}</p>
          <p style={{ color: '#a0aec0' }}>{`Volume: ${payload[0].payload.volume.toFixed(0)}`}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <Container>
      <Title>
        📈 Biểu đồ giá - {selectedCrypto}
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <TimeFrameButtons>
            {['1H', '4H', '1D', '1W'].map(tf => (
              <TimeButton
                key={tf}
                active={timeFrame === tf}
                onClick={() => setTimeFrame(tf)}
              >
                {tf}
              </TimeButton>
            ))}
          </TimeFrameButtons>
          <TimeButton
            active={isRealtime}
            onClick={() => setIsRealtime(!isRealtime)}
            style={{ fontSize: '10px', padding: '4px 8px' }}
          >
            {isRealtime ? '🟢 Live' : '🔴 Pause'}
          </TimeButton>
        </div>
      </Title>

      <ChartContainer>
        <div style={{ 
          position: 'absolute', 
          top: '10px', 
          right: '10px', 
          fontSize: '12px', 
          color: isRealtime ? '#00ff88' : '#a0aec0',
          zIndex: 10 
        }}>
          {isRealtime ? '🟢 Live' : '🔴 Paused'}
        </div>
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" stroke="rgba(255, 255, 255, 0.1)" />
            <XAxis 
              dataKey="time" 
              stroke="#a0aec0"
              fontSize={12}
              tick={{ fill: '#a0aec0' }}
            />
            <YAxis 
              stroke="#a0aec0"
              fontSize={12}
              tick={{ fill: '#a0aec0' }}
              domain={['dataMin - 100', 'dataMax + 100']}
            />
            <Tooltip content={<CustomTooltip />} />
            <Line 
              type="monotone" 
              dataKey="price" 
              stroke="#00ff88" 
              strokeWidth={2}
              dot={false}
              activeDot={{ r: 4, fill: '#00ff88' }}
              connectNulls={true}
              isAnimationActive={false}
            />
          </LineChart>
        </ResponsiveContainer>
      </ChartContainer>
    </Container>
  );
};

export default TradingChart;
