import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  backdrop-filter: blur(10px);
  padding: 20px;
`;

const Title = styled.h3`
  margin-bottom: 15px;
  color: #a0aec0;
  font-size: 16px;
  text-align: center;
`;

const CryptoGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 10px;
  max-height: 400px;
  overflow-y: auto;
`;

const CryptoItem = styled.div`
  background: ${props => props.selected ? 'rgba(102, 126, 234, 0.3)' : 'rgba(255, 255, 255, 0.05)'};
  border: 1px solid ${props => props.selected ? 'rgba(102, 126, 234, 0.5)' : 'rgba(255, 255, 255, 0.1)'};
  border-radius: 8px;
  padding: 12px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(102, 126, 234, 0.2);
    transform: translateY(-2px);
  }
`;

const CryptoName = styled.div`
  font-weight: bold;
  font-size: 14px;
  margin-bottom: 4px;
`;

const CryptoPrice = styled.div`
  font-size: 12px;
  color: #00ff88;
  font-weight: bold;
  margin-bottom: 2px;
`;

const CryptoChange = styled.div`
  font-size: 11px;
  color: ${props => props.positive ? '#00ff88' : '#ff4757'};
`;

const CryptoList = ({ cryptoData, selectedCrypto, onSelectCrypto }) => {
  // Danh sÃ¡ch táº¥t cáº£ crypto cÃ³ sáºµn
  const allCryptos = [
    'BTC', 'ETH', 'BNB', 'ADA', 'SOL', 'DOT', 'LINK', 'MATIC',
    'XRP', 'DOGE', 'AVAX', 'UNI', 'LTC', 'BCH', 'ATOM', 'NEAR',
    'ALGO', 'VET', 'ICP', 'FIL'
  ];

  return (
    <Container>
      <Title>ðŸ“Š Danh sÃ¡ch Crypto</Title>
      <CryptoGrid>
        {allCryptos.map(crypto => {
          const data = cryptoData[crypto];
          const isSelected = selectedCrypto === crypto;

          return (
            <CryptoItem
              key={crypto}
              selected={isSelected}
              onClick={() => onSelectCrypto(crypto)}
            >
              <CryptoName>{crypto}</CryptoName>
              <CryptoPrice>
                ${data?.price ? data.price.toLocaleString('en-US', { 
                  minimumFractionDigits: 2, 
                  maximumFractionDigits: 2 
                }) : '0.00'}
              </CryptoPrice>
              <CryptoChange positive={data?.change24h >= 0}>
                {data?.change24h ? (data.change24h >= 0 ? '+' : '') + data.change24h.toFixed(2) + '%' : '0.00%'}
              </CryptoChange>
            </CryptoItem>
          );
        })}
      </CryptoGrid>
    </Container>
  );
};

export default CryptoList;
