import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 12px;
  backdrop-filter: blur(10px);
  padding: 20px;
`;

const Title = styled.h3`
  margin-bottom: 15px;
  color: #a0aec0;
  font-size: 16px;
`;

const CryptoItem = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 12px;
  margin: 8px 0;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;
  background: ${props => props.isSelected ? 'rgba(102, 126, 234, 0.2)' : 'transparent'};
  border: 1px solid ${props => props.isSelected ? 'rgba(102, 126, 234, 0.3)' : 'transparent'};

  &:hover {
    background: rgba(255, 255, 255, 0.05);
  }
`;

const CryptoInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
`;

const CryptoSymbol = styled.div`
  font-weight: bold;
  font-size: 16px;
`;

const CryptoName = styled.div`
  font-size: 12px;
  color: #a0aec0;
`;

const PriceInfo = styled.div`
  text-align: right;
`;

const Price = styled.div`
  font-weight: bold;
  font-size: 14px;
`;

const Change = styled.div`
  font-size: 12px;
  color: ${props => props.isPositive ? '#00ff88' : '#ff4757'};
`;

const getCryptoName = (symbol) => {
  const names = {
    BTC: 'Bitcoin',
    ETH: 'Ethereum',
    BNB: 'Binance Coin',
    ADA: 'Cardano',
    SOL: 'Solana'
  };
  return names[symbol] || symbol;
};

const CryptoList = ({ cryptoData, selectedCrypto, onSelectCrypto }) => {
  return (
    <Container>
      <Title>ðŸ“Š Danh sÃ¡ch Crypto</Title>
      {Object.entries(cryptoData).map(([symbol, data]) => (
        <CryptoItem
          key={symbol}
          isSelected={selectedCrypto === symbol}
          onClick={() => onSelectCrypto(symbol)}
        >
          <CryptoInfo>
            <div>
              <CryptoSymbol>{symbol}</CryptoSymbol>
              <CryptoName>{getCryptoName(symbol)}</CryptoName>
            </div>
          </CryptoInfo>
          <PriceInfo>
            <Price>${data.price?.toLocaleString()}</Price>
            <Change isPositive={data.change24h > 0}>
              {data.change24h > 0 ? '+' : ''}{data.change24h?.toFixed(2)}%
            </Change>
          </PriceInfo>
        </CryptoItem>
      ))}
    </Container>
  );
};

export default CryptoList;
